import React, { useState, useEffect } from "react";
import { Table, Card, Typography, Spin, Empty, Tag, Image } from "antd";
import { CheckCircleOutlined } from "@ant-design/icons";
import axios from "axios";

const { Title, Text } = Typography;

const VoteHistory = ({ account }) => {
  const [loading, setLoading] = useState(true);
  const [voteHistory, setVoteHistory] = useState([]);

  useEffect(() => {
    if (account) {
      fetchVoteHistory();
    }
  }, [account]);

  const fetchVoteHistory = async () => {
    try {
      setLoading(true);
      const response = await axios.get(
        `http://localhost:8080/userVoteHistory?address=${account}`
      );
      if (response.data && response.data.votes) {
        setVoteHistory(response.data.votes);
      }
    } catch (error) {
      console.error("获取投票历史失败:", error);
    } finally {
      setLoading(false);
    }
  };

  const columns = [
    {
      title: "项目ID",
      dataIndex: "projectId",
      key: "projectId",
      render: (text) => <Text code>{text}</Text>,
    },
    {
      title: "项目名称",
      dataIndex: "projectName",
      key: "projectName",
    },
    {
      title: "候选人",
      dataIndex: "candidateName",
      key: "candidateName",
      render: (text, record) => (
        <div style={{ display: "flex", alignItems: "center" }}>
          <Image
            src={record.candidateImage}
            alt={text}
            width={40}
            height={40}
            style={{ borderRadius: "50%", marginRight: 10 }}
            fallback="data:image/png;base64,"
          />
          {text}
        </div>
      ),
    },
    {
      title: "区块哈希",
      dataIndex: "blockHash",
      key: "blockHash",
      ellipsis: true,
      render: (text) => (
        <Text copyable style={{ width: 180 }} ellipsis>
          {text}
        </Text>
      ),
    },
    {
      title: "状态",
      key: "status",
      render: () => (
        <Tag icon={<CheckCircleOutlined />} color="success">
          投票成功
        </Tag>
      ),
    },
  ];

  return (
    <Card className="vote-history-card">
      <Title level={3}>我的投票历史</Title>
      <div className="vote-history-content">
        <Spin spinning={loading}>
          {voteHistory.length > 0 ? (
            <Table
              dataSource={voteHistory}
              columns={columns}
              rowKey={(record) => `${record.projectId}-${record.candidateId}`}
              pagination={voteHistory.length > 10 ? { pageSize: 10 } : false}
            />
          ) : (
            <Empty
              description="您还没有参与任何投票"
              style={{ marginTop: 40 }}
            />
          )}
        </Spin>
      </div>
    </Card>
  );
};

export default VoteHistory;
